import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiSearch, FiGrid, FiTrendingUp, FiUsers, FiBarChart2, FiTarget, FiFolder } from 'react-icons/fi';
import './Dashboard.css';

function Dashboard({ user }) {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeSection, setActiveSection] = useState('overview');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // For local development without backend, use mock data
      if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
        // Mock dashboard data
        const mockData = {
          data: {
            totalUsers: 1,
            recentActivity: [
              { action: 'User logged in', timestamp: new Date() },
              { action: 'New user registered', timestamp: new Date() }
            ]
          }
        };
        setDashboardData(mockData);
        setLoading(false);
        return;
      }
      
      // Production code - actual API call
      const response = await axios.get('/api/dashboard');
      setDashboardData(response.data);
    } catch (error) {
      setError('Failed to load dashboard data');
      console.error('Dashboard error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSectionChange = (section) => {
    setActiveSection(section);
  };

  const renderContent = () => {
    switch (activeSection) {
      case 'overview':
        return (
          <>
            <h1>Welcome to your Dashboard, {user.username}!</h1>
            
            {error && (
              <div className="alert alert-error">
                {error}
              </div>
            )}
            
            <div className="dashboard-grid">
              <div className="dashboard-card">
                <h3>Profile Information</h3>
                <p><strong>Username:</strong> {user.username}</p>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>User ID:</strong> {user.id}</p>
              </div>
              
              {dashboardData && (
                <>
                  <div className="dashboard-card">
                    <h3>Statistics</h3>
                    <p><strong>Total Users:</strong> {dashboardData.data.totalUsers}</p>
                    <p><strong>Status:</strong> Active</p>
                  </div>
                  
                  <div className="dashboard-card">
                    <h3>Recent Activity</h3>
                    {dashboardData.data.recentActivity.map((activity, index) => (
                      <div key={index} style={{ marginBottom: '0.5rem' }}>
                        <p>{activity.action}</p>
                        <small style={{ color: '#666' }}>
                          {new Date(activity.timestamp).toLocaleString()}
                        </small>
                      </div>
                    ))}
                  </div>
                </>
              )}
            </div>
            
            <div className="dashboard-card">
              <h3>Quick Actions</h3>
              <p>This is where you can add more functionality to your dashboard.</p>
              <p>Some ideas:</p>
              <ul>
                <li>User management</li>
                <li>Data analytics</li>
                <li>Settings configuration</li>
                <li>File uploads</li>
              </ul>
            </div>
          </>
        );
      
      case 'leads-flow':
        return (
          <>
            <h1>Leads Flow</h1>
            <div className="dashboard-card">
              <h3>Lead Generation Analytics</h3>
              <p>Monitor and analyze your lead generation performance across all channels.</p>
              <p>Track conversion rates, source effectiveness, and lead quality metrics.</p>
              <ul>
                <li>Website traffic conversion</li>
                <li>Social media leads</li>
                <li>Email campaign responses</li>
                <li>Referral tracking</li>
              </ul>
            </div>
          </>
        );
      
      case 'client-leads':
        return (
          <>
            <h1>Client Leads</h1>
            <div className="dashboard-card">
              <h3>Client Lead Management</h3>
              <p>Manage and track all your client leads in one centralized location.</p>
              <p>View lead status, contact information, and conversion progress.</p>
              <ul>
                <li>New leads requiring follow-up</li>
                <li>Qualified prospects</li>
                <li>Active negotiations</li>
                <li>Converted clients</li>
              </ul>
            </div>
          </>
        );
      
      case 'campaign-metrics':
        return (
          <>
            <h1>Campaign Metrics</h1>
            <div className="dashboard-card">
              <h3>Marketing Campaign Performance</h3>
              <p>Comprehensive analytics for all your marketing campaigns.</p>
              <p>Track ROI, engagement rates, and campaign effectiveness.</p>
              <ul>
                <li>Click-through rates</li>
                <li>Conversion metrics</li>
                <li>Cost per acquisition</li>
                <li>Revenue attribution</li>
              </ul>
            </div>
          </>
        );
      
      case 'conversion-tracking':
        return (
          <>
            <h1>Conversion Tracking</h1>
            <div className="dashboard-card">
              <h3>Conversion Analytics</h3>
              <p>Track and optimize your conversion funnel performance.</p>
              <p>Identify bottlenecks and opportunities for improvement.</p>
              <ul>
                <li>Funnel analysis</li>
                <li>Goal completion rates</li>
                <li>A/B testing results</li>
                <li>User behavior insights</li>
              </ul>
            </div>
          </>
        );
      
      case 'our-projects':
        return (
          <>
            <h1>Our Projects</h1>
            <div className="dashboard-card">
              <h3>Project Portfolio</h3>
              <p>View and manage all active and completed projects.</p>
              <p>Track project progress, deadlines, and team collaboration.</p>
              <ul>
                <li>Active projects</li>
                <li>Project timelines</li>
                <li>Team assignments</li>
                <li>Client deliverables</li>
              </ul>
            </div>
          </>
        );
      
      default:
        return (
          <div className="dashboard-card">
            <h3>Section Not Found</h3>
            <p>The requested section could not be found.</p>
          </div>
        );
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <div className="dashboard-sidebar">
          <div className="sidebar-search">
            <FiSearch className="search-icon" />
            <input type="text" placeholder="Search" className="search-input" />
          </div>
        </div>
        <div className="dashboard-main">
          <div className="dashboard-content">
            <h1>Dashboard</h1>
            <p>Loading...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="dashboard-sidebar">
        {/* Search */}
        <div className="sidebar-search">
          <FiSearch className="search-icon" />
          <input type="text" placeholder="Search" className="search-input" />
        </div>
        
        {/* Dashboard Section */}
        <div className="sidebar-section">
          <h4 className="sidebar-section-title">DASHBOARD</h4>
          <div 
            className={`sidebar-item ${activeSection === 'overview' ? 'active' : ''}`}
            onClick={() => handleSectionChange('overview')}
          >
            <FiGrid className="sidebar-icon" />
            <span>Overview</span>
          </div>
        </div>
        
        {/* Data Center Section */}
        <div className="sidebar-section">
          <h4 className="sidebar-section-title">DATA CENTER</h4>
          <div 
            className={`sidebar-item ${activeSection === 'leads-flow' ? 'active' : ''}`}
            onClick={() => handleSectionChange('leads-flow')}
          >
            <FiTrendingUp className="sidebar-icon" />
            <span>Leads Flow</span>
          </div>
          <div 
            className={`sidebar-item ${activeSection === 'client-leads' ? 'active' : ''}`}
            onClick={() => handleSectionChange('client-leads')}
          >
            <FiUsers className="sidebar-icon" />
            <span>Client Leads</span>
          </div>
          <div 
            className={`sidebar-item ${activeSection === 'campaign-metrics' ? 'active' : ''}`}
            onClick={() => handleSectionChange('campaign-metrics')}
          >
            <FiBarChart2 className="sidebar-icon" />
            <span>Campaign Metrics</span>
          </div>
          <div 
            className={`sidebar-item ${activeSection === 'conversion-tracking' ? 'active' : ''}`}
            onClick={() => handleSectionChange('conversion-tracking')}
          >
            <FiTarget className="sidebar-icon" />
            <span>Conversion Tracking</span>
          </div>
        </div>
        
        {/* Projects Section */}
        <div className="sidebar-section">
          <h4 className="sidebar-section-title">PROJECTS</h4>
          <div 
            className={`sidebar-item ${activeSection === 'our-projects' ? 'active' : ''}`}
            onClick={() => handleSectionChange('our-projects')}
          >
            <FiFolder className="sidebar-icon" />
            <span>Our Projects</span>
          </div>
        </div>
      </div>
      
      {/* Main Content */}
      <div className="dashboard-main">
        <div className="dashboard-content">
          <h1>Welcome to your Dashboard, {user.username}!</h1>
          
          {error && (
            <div className="alert alert-error">
              {error}
            </div>
          )}
          
          <div className="dashboard-grid">
            <div className="dashboard-card">
              <h3>Profile Information</h3>
              <p><strong>Username:</strong> {user.username}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>User ID:</strong> {user.id}</p>
            </div>
            
            {dashboardData && (
              <>
                <div className="dashboard-card">
                  <h3>Statistics</h3>
                  <p><strong>Total Users:</strong> {dashboardData.data.totalUsers}</p>
                  <p><strong>Status:</strong> Active</p>
                </div>
                
                <div className="dashboard-card">
                  <h3>Recent Activity</h3>
                  {dashboardData.data.recentActivity.map((activity, index) => (
                    <div key={index} style={{ marginBottom: '0.5rem' }}>
                      <p>{activity.action}</p>
                      <small style={{ color: '#666' }}>
                        {new Date(activity.timestamp).toLocaleString()}
                      </small>
                    </div>
                  ))}
                </div>
              </>
            )}
          </div>
          
          <div className="dashboard-card">
            <h3>Quick Actions</h3>
            <p>This is where you can add more functionality to your dashboard.</p>
            <p>Some ideas:</p>
            <ul>
              <li>User management</li>
              <li>Data analytics</li>
              <li>Settings configuration</li>
              <li>File uploads</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
