import React, { useState, useEffect, useCallback } from 'react';
import { useRole } from '../contexts/RoleContext';
import { toast } from 'sonner';
import { FiSearch, FiFilter, FiPlus, FiEdit3, FiTrash2, FiUser, FiShield, FiSettings, FiEye, FiUsers, FiUserCheck, FiUserX, FiClock, FiMail, FiPhone, FiMapPin, FiCalendar, FiStar, FiActivity } from 'react-icons/fi';
import api from '../config/api';
import './UsersRoles.css';


function UsersRoles() {
  const { users, setUsers, updateUserRole, currentUser, updateUserPermissions } = useRole();
  const [showSidePanel, setShowSidePanel] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [filterRole, setFilterRole] = useState('All');
  const [filterStatus, setFilterStatus] = useState('All');
  const [showAddUserModal, setShowAddUserModal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    inactive: 0,
    admins: 0
  });

  // Fetch users from backend
  const fetchUsers = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await api.get('/users');
      if (response.data && response.data.users) {
        setUsers(response.data.users);
        updateStats(response.data.users);
      }
    } catch (error) {
      console.error('Error fetching users:', error);
      setError('Failed to fetch users');
    } finally {
      setIsLoading(false);
    }
  }, [setUsers]);

  // Update statistics
  const updateStats = (userList) => {
    const total = userList.length;
    const active = userList.filter(user => user.status === 'Active').length;
    const inactive = userList.filter(user => user.status === 'Inactive').length;
    const admins = userList.filter(user => user.role === 'Admin').length;
    
    setStats({ total, active, inactive, admins });
  };

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  useEffect(() => {
    updateStats(users);
  }, [users]);

  // Filter users based on search and filters
  const filteredUsers = users.filter(user => {
    const matchesSearch = user.name?.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         user.username?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         user.email?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesRole = filterRole === 'All' || user.role === filterRole;
    const matchesStatus = filterStatus === 'All' || user.status === filterStatus;
    
    return matchesSearch && matchesRole && matchesStatus;
  });

  const handleUserClick = (user) => {
    setSelectedUser(user);
    setShowSidePanel(true);
  };

  const handleCloseSidePanel = () => {
    setShowSidePanel(false);
    setSelectedUser(null);
  };

  const handleRoleChange = async (userId, newRole) => {
    try {
      const result = await updateUserRole(userId, newRole);
      if (result.success) {
        toast.success('User role updated successfully');
        fetchUsers(); // Refresh the list
      } else {
        toast.error('Failed to update user role: ' + result.error);
      }
    } catch (error) {
      console.error('Error updating user role:', error);
      toast.error('Failed to update user role');
    }
  };


  const handlePermissionChange = async (userId, permission, value) => {
    try {
      const result = await updateUserPermissions(userId, { [permission]: value });
      if (result.success) {
        toast.success('Permission updated successfully');
        fetchUsers(); // Refresh the list
      } else {
        toast.error('Failed to update permission: ' + result.error);
      }
    } catch (error) {
      console.error('Error updating permission:', error);
      toast.error('Failed to update permission');
    }
  };

  const handleStatusChange = async (userId, newStatus) => {
    try {
      const result = await updateUserRole(userId, null, newStatus);
      if (result.success) {
        toast.success('User status updated successfully');
        fetchUsers(); // Refresh the list
      } else {
        toast.error('Failed to update user status: ' + result.error);
      }
    } catch (error) {
      console.error('Error updating user status:', error);
      toast.error('Failed to update user status');
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      if (window.confirm('Are you sure you want to delete this user? This action cannot be undone.')) {
        const response = await api.delete(`/users/${userId}`);
        if (response.status === 200) {
          setUsers(prevUsers => prevUsers.filter(user => user.id !== userId));
          toast.success('User deleted successfully');
        }
      }
    } catch (error) {
      console.error('Error deleting user:', error);
      toast.error('Failed to delete user');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Active': return '#10B981';
      case 'Inactive': return '#6B7280';
      case 'Terminated': return '#EF4444';
      case 'In Review': return '#F59E0B';
      default: return '#6B7280';
    }
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'Admin': return '#8B5CF6';
      case 'Project Manager': return '#10B981';
      case 'Data Analyst': return '#3B82F6';
      case 'Data Miner': return '#8B5CF6';
      case 'Social Media Manager': return '#F59E0B';
      case 'Human Resource': return '#EC4899';
      case 'Graphic Designer': return '#06B6D4';
      case 'Web Developer': return '#10B981';
      case 'SEO Manager': return '#06B6D4';
      default: return '#6B7280';
    }
  };

  return (
    <div className="users-roles-container">
      {/* Header with Stats */}
      <div className="users-roles-header">
        <div className="header-content">
          <div className="header-text">
            <h1>Users & Roles Management</h1>
            <p>Manage user accounts, roles, and permissions across your organization</p>
          </div>
          <div className="header-actions">
            {currentUser?.role === 'Admin' && (
              <button 
                className="add-user-btn"
                onClick={() => setShowAddUserModal(true)}
              >
                <FiPlus /> Add User
              </button>
            )}
          </div>
        </div>
        
        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon">
              <FiUsers />
            </div>
            <div className="stat-content">
              <div className="stat-value">{stats.total}</div>
              <div className="stat-label">Total Users</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon active">
              <FiUserCheck />
            </div>
            <div className="stat-content">
              <div className="stat-value">{stats.active}</div>
              <div className="stat-label">Active Users</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon inactive">
              <FiUserX />
            </div>
            <div className="stat-content">
              <div className="stat-value">{stats.inactive}</div>
              <div className="stat-label">Inactive Users</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon admin">
              <FiShield />
            </div>
            <div className="stat-content">
              <div className="stat-value">{stats.admins}</div>
              <div className="stat-label">Administrators</div>
            </div>
          </div>
        </div>
      </div>

      {error && (
        <div className="error-message">
          <FiActivity />
          {error}
        </div>
      )}

      {/* Filters and Search */}
      <div className="filters-section">
        <div className="search-container">
          <FiSearch className="search-icon" />
          <input
            type="text"
            placeholder="Search users by name, email..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
        
        <div className="filters-container">
          <div className="filter-group">
            <FiFilter className="filter-icon" />
            <select 
              value={filterRole} 
              onChange={(e) => setFilterRole(e.target.value)}
              className="filter-select"
            >
              <option value="All">All Roles</option>
              <option value="Admin">Admin</option>
              <option value="Project Manager">Project Manager</option>
              <option value="Data Analyst">Data Analyst</option>
              <option value="Data Miner">Data Miner</option>
              <option value="Social Media Manager">Social Media Manager</option>
              <option value="Human Resource">Human Resource</option>
              <option value="Graphic Designer">Graphic Designer</option>
              <option value="Web Developer">Web Developer</option>
              <option value="SEO Manager">SEO Manager</option>
            </select>
          </div>
          
          <div className="filter-group">
            <select 
              value={filterStatus} 
              onChange={(e) => setFilterStatus(e.target.value)}
              className="filter-select"
            >
              <option value="All">All Status</option>
              <option value="Active">Active</option>
              <option value="Inactive">Inactive</option>
              <option value="Terminated">Terminated</option>
              <option value="In Review">In Review</option>
            </select>
          </div>
        </div>
      </div>

      {isLoading ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading users...</p>
        </div>
      ) : (
        <div className="users-grid">
          {filteredUsers.map((user) => (
            <div key={user.id} className="user-card">
              <div className="user-card-header">
                <div className="user-avatar">
                  <div className="avatar-icon">
                    {user.name ? user.name.charAt(0).toUpperCase() : 'ðŸ‘¤'}
                  </div>
                  <div className={`status-indicator ${user.status?.toLowerCase()}`}></div>
                </div>
                <div className="user-basic-info">
                  <h3 className="user-name">{user.name || user.username}</h3>
                  <p className="user-email">{user.email}</p>
                  <div className="user-meta">
                    <span 
                      className="role-badge"
                      style={{ backgroundColor: getRoleColor(user.role) }}
                    >
                      {user.role}
                    </span>
                    <span 
                      className="status-badge"
                      style={{ backgroundColor: getStatusColor(user.status) }}
                    >
                      {user.status}
                    </span>
                  </div>
                </div>
              </div>

              <div className="user-permissions-preview">
                <div className="permissions-count">
                  <FiSettings />
                  <span>
                    {Object.values(user.permissions || {}).filter(Boolean).length} permissions
                  </span>
                </div>
                <div className="access-tags">
                  {user.access?.slice(0, 3).map((access, index) => (
                    <span key={index} className="access-tag">{access}</span>
                  ))}
                  {user.access?.length > 3 && (
                    <span className="access-tag more">+{user.access.length - 3}</span>
                  )}
                </div>
              </div>

              <div className="user-card-actions">
                <button 
                  className="action-btn view"
                  onClick={() => handleUserClick(user)}
                  title="View Details"
                >
                  <FiEye />
                </button>
                
                {currentUser?.role === 'Admin' && (
                  <>
                    <button 
                      className="action-btn edit"
                      onClick={() => handleUserClick(user)}
                      title="Edit User"
                    >
                      <FiEdit3 />
                    </button>
                    <button 
                      className="action-btn delete"
                      onClick={() => handleDeleteUser(user.id)}
                      title="Delete User"
                    >
                      <FiTrash2 />
                    </button>
                  </>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      {filteredUsers.length === 0 && !isLoading && (
        <div className="no-users-found">
          <FiUser className="no-users-icon" />
          <h3>No users found</h3>
          <p>Try adjusting your search or filter criteria</p>
        </div>
      )}

      {showSidePanel && selectedUser && (
        <UserDetailsSidePanel
          user={selectedUser}
          onClose={handleCloseSidePanel}
          isVisible={showSidePanel}
          setUsers={setUsers}
          handlePermissionChange={handlePermissionChange}
          currentUser={currentUser}
          fetchUsers={fetchUsers}
        />
      )}
    </div>
  );
}

// User Details Side Panel Component
function UserDetailsSidePanel({ user, onClose, isVisible, setUsers, handlePermissionChange, currentUser }) {
  if (!user) return null;

  // Mock additional user data - in real app this would come from API
  const userDetails = {
    ...user,
    currentProjects: 5,
    totalProjects: 21,
    joinDate: '10 - 04 - 2024',
    phone: '+91 9098989281',
    location: 'Coffee Estate Homestay, Mullayanagiri Road, Kaimara, Chikmagalur, Karnataka - 577101, India',
    languages: ['English', 'Hindi'],
    maritalStatus: 'Single',
    employmentId: '0020192',
    responseTime: '24m'
  };

  return (
    <>
      {/* Overlay */}
      <div 
        className={`side-panel-overlay ${isVisible ? 'visible' : ''}`}
        onClick={onClose}
      />
      
      {/* Side Panel */}
      <div className={`user-details-side-panel ${isVisible ? 'visible' : ''}`}>
        {/* Header */}
        <div className="side-panel-header">
          <h3>User Preview</h3>
          <button className="close-btn" onClick={onClose}>âœ•</button>
        </div>

        {/* User Info */}
        <div className="side-panel-content">
          <div className="user-profile-section">
            <div className="user-avatar-large">
              <div className="avatar-icon-large">ðŸ‘¤</div>
            </div>
            <div className="user-info-text">
              <h2>{userDetails.name}</h2>
              <div className="user-status-indicator">
                <span className="status-dot-green"></span>
                <span className="status-text">Online</span>
              </div>
              <p className="user-email-large">{userDetails.email}</p>
            </div>
          </div>

          {/* Project Stats */}
          <div className="project-stats">
            <div className="stat-item">
              <div className="stat-label">Currently</div>
              <div className="stat-value">{userDetails.currentProjects}</div>
            </div>
            <div className="stat-item">
              <div className="stat-label">Total Projects</div>
              <div className="stat-value">{userDetails.totalProjects}</div>
            </div>
          </div>

          {/* User Details Section */}
          <div className="details-section">
            <h4>User Details</h4>
            <div className="detail-grid-two-column">
              <div className="detail-row">
                <div className="detail-item-left">
                  <span className="detail-label">E-mail</span>
                  <span className="detail-value">{userDetails.email}</span>
                </div>
                <div className="detail-item-right">
                  <span className="detail-label">Phone</span>
                  <span className="detail-value">{userDetails.phone}</span>
                </div>
              </div>
              <div className="detail-row">
                <div className="detail-item-left">
                  <span className="detail-label">Location</span>
                  <span className="detail-value">{userDetails.location}</span>
                </div>
                <div className="detail-item-right">
                  <span className="detail-label">Languages</span>
                  <span className="detail-value">{userDetails.languages.join(', ')}</span>
                </div>
              </div>
              <div className="detail-row">
                <div className="detail-item-left">
                  <span className="detail-label">Marital Status</span>
                  <span className="detail-value">{userDetails.maritalStatus}</span>
                </div>
                <div className="detail-item-right">
                  <span className="detail-label">Employment ID</span>
                  <span className="detail-value">{userDetails.employmentId}</span>
                </div>
              </div>
            </div>
          </div>
          
          {/* Permissions Section */}
          <div className="details-section">
            <h4>Permissions (Click to modify)</h4>
            <div className="permissions-grid">
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.dashboard || false}
                    onChange={(e) => handlePermissionChange(user.id, 'dashboard', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">Dashboard</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.dataCenter || false}
                    onChange={(e) => handlePermissionChange(user.id, 'dataCenter', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">Data Center</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.projects || false}
                    onChange={(e) => handlePermissionChange(user.id, 'projects', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">Projects</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.teamCommunication || false}
                    onChange={(e) => handlePermissionChange(user.id, 'teamCommunication', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">Team Communication</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.digitalMediaManagement || false}
                    onChange={(e) => handlePermissionChange(user.id, 'digitalMediaManagement', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">Digital Media Management</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.marketing || false}
                    onChange={(e) => handlePermissionChange(user.id, 'marketing', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">Marketing</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.hrManagement || false}
                    onChange={(e) => handlePermissionChange(user.id, 'hrManagement', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">HR Management</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.financeManagement || false}
                    onChange={(e) => handlePermissionChange(user.id, 'financeManagement', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">Finance Management</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.seoManagement || false}
                    onChange={(e) => handlePermissionChange(user.id, 'seoManagement', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">SEO Management</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.internalPolicies || false}
                    onChange={(e) => handlePermissionChange(user.id, 'internalPolicies', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">Internal Policies</span>
                </label>
              </div>
              <div className="permission-item">
                <label className="permission-checkbox">
                  <input
                    type="checkbox"
                    checked={user.permissions?.settingsConfiguration || false}
                    onChange={(e) => handlePermissionChange(user.id, 'settingsConfiguration', e.target.checked)}
                    disabled={currentUser?.role !== 'Admin'}
                  />
                  <span className="permission-label">Settings & Configuration</span>
                </label>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default UsersRoles;
