import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Sidebar from './Sidebar';
import Overview from './Overview';
import LeadsFlow from './LeadsFlow';
import ClientLeads from './ClientLeads';
import CampaignMetrics from './CampaignMetrics';
import ConversionTracking from './ConversionTracking';
import OurProjects from './OurProjects';
import ProjectDelivered from './ProjectDelivered';
import CaseStudies from './CaseStudies';
import Services from './Services';
import EmailMarketing from './EmailMarketing';
import CampaignSetup from './CampaignSetup';
import EmailTemplate from './EmailTemplate';
import EmailAnalytics from './EmailAnalytics';
import WhatsAppMarketing from './WhatsAppMarketing';
import ReferralClient from './ReferralClient';
import './Dashboard.css';

function Dashboard({ user }) {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeSection, setActiveSection] = useState('overview');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // For local development without backend, use mock data
      if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
        // Mock dashboard data
        const mockData = {
          data: {
            totalUsers: 1,
            recentActivity: [
              { action: 'User logged in', timestamp: new Date() },
              { action: 'New user registered', timestamp: new Date() }
            ]
          }
        };
        setDashboardData(mockData);
        setLoading(false);
        return;
      }
      
      // Production code - actual API call
      const response = await axios.get('/api/dashboard');
      setDashboardData(response.data);
    } catch (error) {
      setError('Failed to load dashboard data');
      console.error('Dashboard error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSectionChange = (section) => {
    setActiveSection(section);
  };

  const renderContent = () => {
    switch (activeSection) {
      case 'overview':
        return <Overview user={user} dashboardData={dashboardData} error={error} />;
      case 'leads-flow':
        return <LeadsFlow />;
      case 'client-leads':
        return <ClientLeads />;
      case 'campaign-metrics':
        return <CampaignMetrics />;
      case 'conversion-tracking':
        return <ConversionTracking />;
      case 'our-projects':
        return <OurProjects />;
      default:
        return (
          <div className="dashboard-card">
            <h3>Section Not Found</h3>
            <p>The requested section could not be found.</p>
          </div>
        );
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <Sidebar activeSection={activeSection} onSectionChange={handleSectionChange} />
        <div className="dashboard-main">
          <div className="dashboard-content">
            <h1>Dashboard</h1>
            <p>Loading...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <Sidebar activeSection={activeSection} onSectionChange={handleSectionChange} />
      
      {/* Main Content */}
      <div className="dashboard-main">
        <div className="dashboard-content">
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
