import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login({ onLogin }) {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    const result = await onLogin(formData.email, formData.password);
    
    if (result.success) {
      setMessage('Login successful!');
      setTimeout(() => {
        navigate('/dashboard');
      }, 1000);
    } else {
      setMessage(result.message);
    }
    
    setLoading(false);
  };

  const handleGoogleLogin = () => {
    // Placeholder for Google OAuth integration
    console.log('Google login clicked');
  };

  return (
    <div className="login-page">
      <div className="login-split-container">
        {/* Left Side - Welcome Content */}
        <div className="login-left-side">
          <div className="powered-by-logo">
            <img src="/src/components/image/powered_by_noxtm.svg" alt="Powered by Noxtm" />
          </div>
          <div className="welcome-content">
            <h1 className="welcome-title">Discover Noxtm Studio</h1>
            <p className="welcome-description">
              At Noxtm Studio, we don't just market, we create experiences 
              that connect, inspire, and convert.
            </p>
          </div>
        </div>

        {/* Right Side - Login Form */}
        <div className="login-right-side">
          <div className="login-form">
            <h1 className="login-title">Login into your Account</h1>
            
            {message && (
              <div className={`alert ${message.includes('successful') ? 'alert-success' : 'alert-error'}`}>
                {message}
              </div>
            )}
            
            {/* Social Login Buttons */}
            <div className="social-login-section">
              <button 
                type="button" 
                className="social-login-btn google-btn"
                onClick={handleGoogleLogin}
              >
                <svg className="social-icon" viewBox="0 0 24 24" width="20" height="20">
                  <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                  <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                  <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                  <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                </svg>
                Continue with Google
              </button>
            </div>

            {/* Email Login Form */}
            <form onSubmit={handleSubmit} className="email-login-form">
              <div className="form-group">
                <label htmlFor="email" className="form-label">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="Enter your email address..."
                  className="form-input"
                  required
                />
              </div>
              
              <div className="form-group">
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="••••••••••••••••••••••••"
                  className="form-input password-input"
                  required
                />
                <div className="forgot-password-container">
                  <button type="button" className="forgot-password-link">Forgot password?</button>
                </div>
              </div>
              
              <button type="submit" className="continue-btn" disabled={loading}>
                {loading ? 'Signing in...' : 'Continue with email'}
              </button>
            </form>

            {/* Create Account Link */}
            <div className="create-account-section">
              <p className="create-account-text">
                Don't have an account? <a href="/signup" className="create-account-link">Create Account</a>
              </p>
            </div>

            {/* Terms and Privacy */}
            <div className="terms-section">
              <p>
                By clicking "Continue with Google/Email" above, you acknowledge that you 
                have read and understood, and agree to Noxtm Studio's{' '}
                <a href="/terms" className="terms-link">Terms & Conditions</a> and{' '}
                <a href="/privacy" className="terms-link">Privacy Policy</a>.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
