import React, { useState, useEffect, useCallback } from 'react';
import { FiSearch, FiFilter, FiEdit3, FiTrash2, FiUser, FiPlus, FiMail, FiPhone, FiMapPin, FiCalendar, FiStar, FiActivity, FiMoreHorizontal, FiDownload, FiUpload, FiEye } from 'react-icons/fi';
import { toast } from 'sonner';
import './ClientLeads.css';

function ClientLeads() {
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [customDateFrom, setCustomDateFrom] = useState('');
  const [customDateTo, setCustomDateTo] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('All');
  const [filterSource, setFilterSource] = useState('All');
  const [isLoading, setIsLoading] = useState(false);
  const [showSidePanel, setShowSidePanel] = useState(false);
  const [selectedLead, setSelectedLead] = useState(null);
  const [newClient, setNewClient] = useState({
    name: '',
    email: '',
    phone: '',
    company: '',
    source: 'Website',
    notes: ''
  });

  // Enhanced mock data for leads
  const [leads, setLeads] = useState([
    { 
      id: 1, 
      name: 'John Smith', 
      email: 'john@example.com', 
      phone: '+1-555-0123', 
      company: 'Tech Corp',
      status: 'New', 
      date: '2024-01-15', 
      source: 'Website',
      notes: 'Interested in web development services',
      lastContact: '2024-01-15',
      priority: 'Medium'
    },
    { 
      id: 2, 
      name: 'Sarah Johnson', 
      email: 'sarah@example.com', 
      phone: '+1-555-0124', 
      company: 'Marketing Inc',
      status: 'In Progress', 
      date: '2024-01-14', 
      source: 'Referral',
      notes: 'Referred by existing client',
      lastContact: '2024-01-14',
      priority: 'High'
    },
    { 
      id: 3, 
      name: 'Mike Davis', 
      email: 'mike@example.com', 
      phone: '+1-555-0125', 
      company: 'StartupXYZ',
      status: 'Qualified', 
      date: '2024-01-13', 
      source: 'Social Media',
      notes: 'Active on LinkedIn, engaged with our content',
      lastContact: '2024-01-13',
      priority: 'High'
    },
    { 
      id: 4, 
      name: 'Emily Brown', 
      email: 'emily@example.com', 
      phone: '+1-555-0126', 
      company: 'Design Studio',
      status: 'Converted', 
      date: '2024-01-12', 
      source: 'Email Campaign',
      notes: 'Converted to paying customer',
      lastContact: '2024-01-12',
      priority: 'Low'
    },
    { 
      id: 5, 
      name: 'David Wilson', 
      email: 'david@example.com', 
      phone: '+1-555-0127', 
      company: 'Enterprise Ltd',
      status: 'New', 
      date: '2024-01-11', 
      source: 'Website',
      notes: 'Downloaded our whitepaper',
      lastContact: '2024-01-11',
      priority: 'Medium'
    },
    { 
      id: 6, 
      name: 'Lisa Anderson', 
      email: 'lisa@example.com', 
      phone: '+1-555-0128', 
      company: 'Consulting Group',
      status: 'In Progress', 
      date: '2024-01-10', 
      source: 'LinkedIn',
      notes: 'Scheduled demo call for next week',
      lastContact: '2024-01-10',
      priority: 'High'
    },
    { 
      id: 7, 
      name: 'Robert Taylor', 
      email: 'robert@example.com', 
      phone: '+1-555-0129', 
      company: 'Retail Chain',
      status: 'Qualified', 
      date: '2024-01-09', 
      source: 'Referral',
      notes: 'Large enterprise opportunity',
      lastContact: '2024-01-09',
      priority: 'High'
    },
    { 
      id: 8, 
      name: 'Jennifer Martinez', 
      email: 'jennifer@example.com', 
      phone: '+1-555-0130', 
      company: 'Non-Profit Org',
      status: 'New', 
      date: '2024-01-08', 
      source: 'Website',
      notes: 'Looking for affordable solutions',
      lastContact: '2024-01-08',
      priority: 'Low'
    },
  ]);

  // Calculate statistics
  const [stats, setStats] = useState({
    total: 0,
    new: 0,
    inProgress: 0,
    qualified: 0,
    converted: 0
  });

  useEffect(() => {
    updateStats();
  }, [leads]);

  const updateStats = () => {
    const total = leads.length;
    const newLeads = leads.filter(lead => lead.status === 'New').length;
    const inProgress = leads.filter(lead => lead.status === 'In Progress').length;
    const qualified = leads.filter(lead => lead.status === 'Qualified').length;
    const converted = leads.filter(lead => lead.status === 'Converted').length;
    
    setStats({ total, new: newLeads, inProgress, qualified, converted });
  };

  const filterOptions = [
    { value: 'all', label: 'All Leads' },
    { value: 'today', label: 'Today' },
    { value: 'yesterday', label: 'Yesterday' },
    { value: 'last7days', label: 'Last 7 Days (1 Week)' },
    { value: 'last30days', label: 'Last 30 Days (1 Month)' },
    { value: 'last90days', label: 'Last 90 Days (3 Months)' },
    { value: 'thismonth', label: 'This Month' },
    { value: 'lastmonth', label: 'Last Month' },
    { value: 'custom', label: 'Custom Date Range' }
  ];

  const statusOptions = ['All', 'New', 'In Progress', 'Qualified', 'Converted', 'Lost'];
  const sourceOptions = ['All', 'Website', 'Referral', 'Social Media', 'Email Campaign', 'LinkedIn', 'Other'];

  const handleAddClientClick = () => {
    setShowAddForm(true);
  };

  const handleAddClient = (e) => {
    e.preventDefault();
    if (newClient.name && newClient.email) {
      const newLead = {
        id: leads.length + 1,
        name: newClient.name,
        email: newClient.email,
        phone: newClient.phone,
        company: newClient.company,
        status: 'New',
        date: new Date().toISOString().split('T')[0],
        source: newClient.source,
        notes: newClient.notes,
        lastContact: new Date().toISOString().split('T')[0],
        priority: 'Medium'
      };
      setLeads([newLead, ...leads]);
      setNewClient({ name: '', email: '', phone: '', company: '', source: 'Website', notes: '' });
      setShowAddForm(false);
      toast.success('Lead added successfully!');
    }
  };

  const handleLeadClick = (lead) => {
    setSelectedLead(lead);
    setShowSidePanel(true);
  };

  const handleCloseSidePanel = () => {
    setShowSidePanel(false);
    setSelectedLead(null);
  };

  const handleDeleteLead = (leadId) => {
    if (window.confirm('Are you sure you want to delete this lead? This action cannot be undone.')) {
      setLeads(leads.filter(lead => lead.id !== leadId));
      toast.success('Lead deleted successfully!');
    }
  };

  const handleStatusChange = (leadId, newStatus) => {
    setLeads(leads.map(lead => 
      lead.id === leadId ? { ...lead, status: newStatus } : lead
    ));
    toast.success('Lead status updated successfully!');
  };

  const getFilteredLeads = () => {
    const today = new Date();
    let filteredLeads = leads.filter(lead => {
      const leadDate = new Date(lead.date);
      
      // Date filtering
      let dateMatch = true;
      switch (selectedFilter) {
        case 'all':
          dateMatch = true;
          break;
        case 'today':
          dateMatch = leadDate.toDateString() === today.toDateString();
          break;
        case 'yesterday':
          const yesterday = new Date(today);
          yesterday.setDate(yesterday.getDate() - 1);
          dateMatch = leadDate.toDateString() === yesterday.toDateString();
          break;
        case 'last7days':
          const weekAgo = new Date(today);
          weekAgo.setDate(weekAgo.getDate() - 7);
          dateMatch = leadDate >= weekAgo;
          break;
        case 'last30days':
          const monthAgo = new Date(today);
          monthAgo.setDate(monthAgo.getDate() - 30);
          dateMatch = leadDate >= monthAgo;
          break;
        case 'last90days':
          const threeMonthsAgo = new Date(today);
          threeMonthsAgo.setDate(threeMonthsAgo.getDate() - 90);
          dateMatch = leadDate >= threeMonthsAgo;
          break;
        case 'thismonth':
          dateMatch = leadDate.getMonth() === today.getMonth() && leadDate.getFullYear() === today.getFullYear();
          break;
        case 'lastmonth':
          const lastMonth = new Date(today);
          lastMonth.setMonth(lastMonth.getMonth() - 1);
          dateMatch = leadDate.getMonth() === lastMonth.getMonth() && leadDate.getFullYear() === lastMonth.getFullYear();
          break;
        case 'custom':
          if (customDateFrom && customDateTo) {
            dateMatch = leadDate >= new Date(customDateFrom) && leadDate <= new Date(customDateTo);
          }
          break;
        default:
          dateMatch = true;
      }

      // Search filtering
      const matchesSearch = searchTerm === '' || 
        lead.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        lead.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        lead.company?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        lead.phone?.includes(searchTerm);

      // Status filtering
      const matchesStatus = filterStatus === 'All' || lead.status === filterStatus;

      // Source filtering
      const matchesSource = filterSource === 'All' || lead.source === filterSource;

      return dateMatch && matchesSearch && matchesStatus && matchesSource;
    });
    
    return filteredLeads;
  };

  const filteredLeads = getFilteredLeads();

  const getStatusColor = (status) => {
    switch (status) {
      case 'New': return '#3B82F6';
      case 'In Progress': return '#F59E0B';
      case 'Qualified': return '#10B981';
      case 'Converted': return '#8B5CF6';
      case 'Lost': return '#EF4444';
      default: return '#6B7280';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High': return '#EF4444';
      case 'Medium': return '#F59E0B';
      case 'Low': return '#10B981';
      default: return '#6B7280';
    }
  };

  return (
    <div className="client-leads-container">
      {/* Header */}
      <div className="client-leads-header">
        <div className="header-content">
          <div className="header-text">
            <h1>Client Leads Management</h1>
            <p>Manage and track your client leads with advanced filtering and analytics.</p>
          </div>
        </div>
      </div>

      {/* Statistics Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-header">
            <div className="stat-title">Total Leads</div>
            <div className="stat-icon" style={{ backgroundColor: '#3B82F6' }}>
              <FiUser />
            </div>
          </div>
          <div className="stat-value">{stats.total}</div>
          <div className="stat-change positive">
            <span>+12%</span>
            <span>from last month</span>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-header">
            <div className="stat-title">New Leads</div>
            <div className="stat-icon" style={{ backgroundColor: '#10B981' }}>
              <FiPlus />
            </div>
          </div>
          <div className="stat-value">{stats.new}</div>
          <div className="stat-change positive">
            <span>+8%</span>
            <span>this week</span>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-header">
            <div className="stat-title">In Progress</div>
            <div className="stat-icon" style={{ backgroundColor: '#F59E0B' }}>
              <FiActivity />
            </div>
          </div>
          <div className="stat-value">{stats.inProgress}</div>
          <div className="stat-change positive">
            <span>+5%</span>
            <span>active leads</span>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-header">
            <div className="stat-title">Converted</div>
            <div className="stat-icon" style={{ backgroundColor: '#8B5CF6' }}>
              <FiStar />
            </div>
          </div>
          <div className="stat-value">{stats.converted}</div>
          <div className="stat-change positive">
            <span>+15%</span>
            <span>conversion rate</span>
          </div>
        </div>
      </div>

      {/* Table Header with Search and Filters */}
      <div className="table-header">
        <div className="table-header-left">
          <h3>All leads ({filteredLeads.length})</h3>
        </div>
        <div className="table-header-right">
          <div className="search-container">
            <FiSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search leads..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>
          <div className="filter-controls">
            <select 
              value={selectedFilter} 
              onChange={(e) => setSelectedFilter(e.target.value)}
              className="filter-select"
            >
              {filterOptions.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <select 
              value={filterStatus} 
              onChange={(e) => setFilterStatus(e.target.value)}
              className="filter-select"
            >
              {statusOptions.map(status => (
                <option key={status} value={status}>
                  {status}
                </option>
              ))}
            </select>
            <select 
              value={filterSource} 
              onChange={(e) => setFilterSource(e.target.value)}
              className="filter-select"
            >
              {sourceOptions.map(source => (
                <option key={source} value={source}>
                  {source}
                </option>
              ))}
            </select>
            <button
              onClick={handleAddClientClick}
              className="add-lead-btn"
            >
              <FiPlus />
              Add Lead
            </button>
          </div>
        </div>
      </div>
      
      {/* Custom Date Range Section */}
      {selectedFilter === 'custom' && (
        <div className="custom-date-range">
          <h3>Custom Date Range</h3>
          <div className="date-inputs">
            <input
              type="date"
              value={customDateFrom}
              onChange={(e) => setCustomDateFrom(e.target.value)}
              className="date-input"
              placeholder="From Date"
            />
            <span>to</span>
            <input
              type="date"
              value={customDateTo}
              onChange={(e) => setCustomDateTo(e.target.value)}
              className="date-input"
              placeholder="To Date"
            />
          </div>
        </div>
      )}

      {/* Add Lead Form */}
      {showAddForm && (
        <div className="add-lead-form">
          <h3>Add New Lead</h3>
          <form onSubmit={handleAddClient}>
            <div className="form-grid">
              <div className="form-group">
                <label className="form-label">Name *</label>
                <input
                  type="text"
                  value={newClient.name}
                  onChange={(e) => setNewClient({...newClient, name: e.target.value})}
                  required
                  className="form-input"
                  placeholder="Enter lead name"
                />
              </div>
              <div className="form-group">
                <label className="form-label">Email *</label>
                <input
                  type="email"
                  value={newClient.email}
                  onChange={(e) => setNewClient({...newClient, email: e.target.value})}
                  required
                  className="form-input"
                  placeholder="Enter email address"
                />
              </div>
              <div className="form-group">
                <label className="form-label">Phone</label>
                <input
                  type="tel"
                  value={newClient.phone}
                  onChange={(e) => setNewClient({...newClient, phone: e.target.value})}
                  className="form-input"
                  placeholder="Enter phone number"
                />
              </div>
              <div className="form-group">
                <label className="form-label">Company</label>
                <input
                  type="text"
                  value={newClient.company}
                  onChange={(e) => setNewClient({...newClient, company: e.target.value})}
                  className="form-input"
                  placeholder="Enter company name"
                />
              </div>
              <div className="form-group">
                <label className="form-label">Source</label>
                <select
                  value={newClient.source}
                  onChange={(e) => setNewClient({...newClient, source: e.target.value})}
                  className="form-select"
                >
                  <option value="Website">Website</option>
                  <option value="Referral">Referral</option>
                  <option value="Social Media">Social Media</option>
                  <option value="Email Campaign">Email Campaign</option>
                  <option value="LinkedIn">LinkedIn</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="form-group">
                <label className="form-label">Notes</label>
                <input
                  type="text"
                  value={newClient.notes}
                  onChange={(e) => setNewClient({...newClient, notes: e.target.value})}
                  className="form-input"
                  placeholder="Add notes about this lead"
                />
              </div>
            </div>
            <div className="form-actions">
              <button
                type="button"
                onClick={() => setShowAddForm(false)}
                className="btn-secondary"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="btn-primary"
              >
                Add Lead
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Leads Table */}
      <div className="leads-table-container">
        {isLoading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading leads...</p>
          </div>
        ) : filteredLeads.length > 0 ? (
          <table className="leads-table">
            <thead>
              <tr>
                <th><input type="checkbox" /></th>
                <th>Lead Details</th>
                <th>Company</th>
                <th>Status</th>
                <th>Source</th>
                <th>Date</th>
                <th>Priority</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {filteredLeads.map(lead => (
                <tr key={lead.id} onClick={() => handleLeadClick(lead)}>
                  <td><input type="checkbox" /></td>
                  <td>
                    <div className="lead-details-cell">
                      <div className="lead-avatar">
                        {lead.name ? lead.name.charAt(0).toUpperCase() : 'ðŸ‘¤'}
                      </div>
                      <div className="lead-basic-info">
                        <h3 className="lead-name">{lead.name}</h3>
                        <p className="lead-email">{lead.email}</p>
                      </div>
                    </div>
                  </td>
                  <td>{lead.company || 'N/A'}</td>
                  <td>
                    <span 
                      className="status-badge"
                      style={{ backgroundColor: getStatusColor(lead.status) }}
                    >
                      {lead.status}
                    </span>
                  </td>
                  <td>
                    <span className="source-badge">{lead.source}</span>
                  </td>
                  <td>{new Date(lead.date).toLocaleDateString()}</td>
                  <td>
                    <span 
                      className="status-badge"
                      style={{ backgroundColor: getPriorityColor(lead.priority) }}
                    >
                      {lead.priority}
                    </span>
                  </td>
                  <td>
                    <div className="actions-menu">
                      <button 
                        className="actions-btn" 
                        onClick={(e) => {
                          e.stopPropagation();
                          handleLeadClick(lead);
                        }}
                      >
                        <FiMoreHorizontal />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="no-leads-found">
            <FiUser className="no-leads-icon" />
            <h3>No leads found</h3>
            <p>Try adjusting your search or filter criteria</p>
          </div>
        )}
      </div>

      {/* Lead Details Side Panel */}
      {showSidePanel && selectedLead && (
        <LeadDetailsSidePanel
          lead={selectedLead}
          onClose={handleCloseSidePanel}
          isVisible={showSidePanel}
          onDelete={handleDeleteLead}
          onStatusChange={handleStatusChange}
        />
      )}
    </div>
  );
}

export default ClientLeads;
