import React, { useState } from 'react';
import './UsersRoles.css';

function UsersRoles() {
  const [users, setUsers] = useState([
    { 
      id: 1, 
      name: 'Rajat Panwar', 
      email: 'rajat@noxtm.com', 
      role: 'Admin', 
      status: 'Active',
      access: ['Data Cluster', 'Projects', 'Finance', 'Digital Media', 'Marketing']
    },
    { 
      id: 2, 
      name: 'Sarah Johnson', 
      email: 'sarah.johnson@company.com', 
      role: 'Project Manager', 
      status: 'Active',
      access: ['Projects']
    },
    { 
      id: 3, 
      name: 'Mike Davis', 
      email: 'mike.davis@company.com', 
      role: 'Social Media Manager', 
      status: 'Active',
      access: ['Digital Media', 'Marketing']
    },
    { 
      id: 4, 
      name: 'Emily Brown', 
      email: 'emily.brown@company.com', 
      role: 'Graphic Designer', 
      status: 'Active',
      access: ['Projects', 'Digital Media']
    },
    { 
      id: 5, 
      name: 'David Wilson', 
      email: 'david.wilson@company.com', 
      role: 'Web Developer', 
      status: 'Active',
      access: ['Projects', 'Data Cluster']
    },
    { 
      id: 6, 
      name: 'Lisa Anderson', 
      email: 'lisa.anderson@company.com', 
      role: 'Data Analyst', 
      status: 'Active',
      access: ['Data Cluster', 'Finance']
    },
    { 
      id: 7, 
      name: 'Tom Miller', 
      email: 'tom.miller@company.com', 
      role: 'Human Resource', 
      status: 'Active',
      access: ['Projects', 'Finance']
    },
    { 
      id: 8, 
      name: 'Kate Taylor', 
      email: 'kate.taylor@company.com', 
      role: 'Data Miner', 
      status: 'Active',
      access: ['Data Cluster']
    },
    { 
      id: 9, 
      name: 'Alex Garcia', 
      email: 'alex.garcia@company.com', 
      role: 'SEO Manager', 
      status: 'Active',
      access: ['Digital Media', 'Marketing']
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  const roles = [
    'Admin',
    'Project Manager', 
    'Data Miner',
    'Data Analyst',
    'Social Media Manager',
    'Human Resource',
    'Graphic Designer',
    'Web Developer',
    'SEO Manager'
  ];

  const handleRoleChange = (userId, newRole) => {
    setUsers(users.map(user => 
      user.id === userId ? { ...user, role: newRole } : user
    ));
  };

  const handleStatusToggle = (userId) => {
    setUsers(users.map(user => 
      user.id === userId ? { 
        ...user, 
        status: user.status === 'Active' ? 'Inactive' : 'Active' 
      } : user
    ));
  };

  return (
    <div className="users-roles-container">
      <div className="users-roles-header">
        <h2>Users & Roles Management</h2>
        <p>Manage user accounts, roles, and permissions across your organization.</p>
      </div>

      <div className="users-table-container">
        <table className="users-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id} className={user.status === 'Inactive' ? 'inactive-user' : ''}>
                <td className="user-name">
                  <div className="user-avatar">
                    {user.name.split(' ').map(n => n[0]).join('')}
                  </div>
                  {user.name}
                </td>
                <td className="user-email">{user.email}</td>
                <td className="user-role">
                  <select 
                    value={user.role} 
                    onChange={(e) => handleRoleChange(user.id, e.target.value)}
                    className="role-select"
                  >
                    {roles.map(role => (
                      <option key={role} value={role}>{role}</option>
                    ))}
                  </select>
                </td>
                <td className="user-status">
                  <span className={`status-badge ${user.status.toLowerCase()}`}>
                    {user.status}
                  </span>
                </td>
                <td className="user-actions">
                  <button 
                    onClick={() => handleStatusToggle(user.id)}
                    className={`status-toggle-btn ${user.status.toLowerCase()}`}
                  >
                    {user.status === 'Active' ? 'Deactivate' : 'Activate'}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="roles-summary">
        <h3>Role Distribution</h3>
        <div className="roles-grid">
          {roles.map(role => {
            const count = users.filter(user => user.role === role).length;
            return (
              <div key={role} className="role-card">
                <span className="role-name">{role}</span>
                <span className="role-count">{count} user{count !== 1 ? 's' : ''}</span>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default UsersRoles;
