import React, { useState, useEffect, useCallback } from 'react';
import { FiSearch, FiFilter, FiEdit3, FiTrash2, FiUser, FiPlus, FiMail, FiPhone, FiMapPin, FiCalendar, FiStar, FiActivity, FiMoreHorizontal, FiDownload, FiUpload, FiEye } from 'react-icons/fi';
import { toast } from 'sonner';
import './ClientLeads.css';

function ClientLeads() {
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [customDateFrom, setCustomDateFrom] = useState('');
  const [customDateTo, setCustomDateTo] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('All');
  const [filterSource, setFilterSource] = useState('All');
  const [isLoading, setIsLoading] = useState(false);
  const [showSidePanel, setShowSidePanel] = useState(false);
  const [selectedLead, setSelectedLead] = useState(null);
  const [newClient, setNewClient] = useState({
    name: '',
    email: '',
    phone: '',
    company: '',
    source: 'Website',
    notes: ''
  });

  // Enhanced mock data for leads
  const [leads, setLeads] = useState([
    { 
      id: 1, 
      name: 'John Smith', 
      email: 'john@example.com', 
      phone: '+1-555-0123', 
      company: 'Tech Corp',
      status: 'New', 
      date: '2024-01-15', 
      source: 'Website',
      notes: 'Interested in web development services',
      lastContact: '2024-01-15',
      priority: 'Medium'
    },
    { 
      id: 2, 
      name: 'Sarah Johnson', 
      email: 'sarah@example.com', 
      phone: '+1-555-0124', 
      company: 'Marketing Inc',
      status: 'In Progress', 
      date: '2024-01-14', 
      source: 'Referral',
      notes: 'Referred by existing client',
      lastContact: '2024-01-14',
      priority: 'High'
    },
    { 
      id: 3, 
      name: 'Mike Davis', 
      email: 'mike@example.com', 
      phone: '+1-555-0125', 
      company: 'StartupXYZ',
      status: 'Qualified', 
      date: '2024-01-13', 
      source: 'Social Media',
      notes: 'Active on LinkedIn, engaged with our content',
      lastContact: '2024-01-13',
      priority: 'High'
    },
    { 
      id: 4, 
      name: 'Emily Brown', 
      email: 'emily@example.com', 
      phone: '+1-555-0126', 
      company: 'Design Studio',
      status: 'Converted', 
      date: '2024-01-12', 
      source: 'Email Campaign',
      notes: 'Converted to paying customer',
      lastContact: '2024-01-12',
      priority: 'Low'
    },
    { 
      id: 5, 
      name: 'David Wilson', 
      email: 'david@example.com', 
      phone: '+1-555-0127', 
      company: 'Enterprise Ltd',
      status: 'New', 
      date: '2024-01-11', 
      source: 'Website',
      notes: 'Downloaded our whitepaper',
      lastContact: '2024-01-11',
      priority: 'Medium'
    },
    { 
      id: 6, 
      name: 'Lisa Anderson', 
      email: 'lisa@example.com', 
      phone: '+1-555-0128', 
      company: 'Consulting Group',
      status: 'In Progress', 
      date: '2024-01-10', 
      source: 'LinkedIn',
      notes: 'Scheduled demo call for next week',
      lastContact: '2024-01-10',
      priority: 'High'
    },
    { 
      id: 7, 
      name: 'Robert Taylor', 
      email: 'robert@example.com', 
      phone: '+1-555-0129', 
      company: 'Retail Chain',
      status: 'Qualified', 
      date: '2024-01-09', 
      source: 'Referral',
      notes: 'Large enterprise opportunity',
      lastContact: '2024-01-09',
      priority: 'High'
    },
    { 
      id: 8, 
      name: 'Jennifer Martinez', 
      email: 'jennifer@example.com', 
      phone: '+1-555-0130', 
      company: 'Non-Profit Org',
      status: 'New', 
      date: '2024-01-08', 
      source: 'Website',
      notes: 'Looking for affordable solutions',
      lastContact: '2024-01-08',
      priority: 'Low'
    },
  ]);

  // Calculate statistics
  const [stats, setStats] = useState({
    total: 0,
    new: 0,
    inProgress: 0,
    qualified: 0,
    converted: 0
  });

  useEffect(() => {
    updateStats();
  }, [leads]);

  const updateStats = () => {
    const total = leads.length;
    const newLeads = leads.filter(lead => lead.status === 'New').length;
    const inProgress = leads.filter(lead => lead.status === 'In Progress').length;
    const qualified = leads.filter(lead => lead.status === 'Qualified').length;
    const converted = leads.filter(lead => lead.status === 'Converted').length;
    
    setStats({ total, new: newLeads, inProgress, qualified, converted });
  };

  const filterOptions = [
    { value: 'all', label: 'All Leads' },
    { value: 'today', label: 'Today' },
    { value: 'yesterday', label: 'Yesterday' },
    { value: 'last7days', label: 'Last 7 Days (1 Week)' },
    { value: 'last30days', label: 'Last 30 Days (1 Month)' },
    { value: 'last90days', label: 'Last 90 Days (3 Months)' },
    { value: 'thismonth', label: 'This Month' },
    { value: 'lastmonth', label: 'Last Month' },
    { value: 'custom', label: 'Custom Date Range' }
  ];

  const statusOptions = ['All', 'New', 'In Progress', 'Qualified', 'Converted', 'Lost'];
  const sourceOptions = ['All', 'Website', 'Referral', 'Social Media', 'Email Campaign', 'LinkedIn', 'Other'];

  const handleAddClientClick = () => {
    setShowAddForm(true);
  };

  const handleAddClient = (e) => {
    e.preventDefault();
    if (newClient.name && newClient.email) {
      const newLead = {
        id: leads.length + 1,
        name: newClient.name,
        email: newClient.email,
        phone: newClient.phone,
        company: newClient.company,
        status: 'New',
        date: new Date().toISOString().split('T')[0],
        source: newClient.source,
        notes: newClient.notes,
        lastContact: new Date().toISOString().split('T')[0],
        priority: 'Medium'
      };
      setLeads([newLead, ...leads]);
      setNewClient({ name: '', email: '', phone: '', company: '', source: 'Website', notes: '' });
      setShowAddForm(false);
      toast.success('Lead added successfully!');
    }
  };

  const handleLeadClick = (lead) => {
    setSelectedLead(lead);
    setShowSidePanel(true);
  };

  const handleCloseSidePanel = () => {
    setShowSidePanel(false);
    setSelectedLead(null);
  };

  const handleDeleteLead = (leadId) => {
    if (window.confirm('Are you sure you want to delete this lead? This action cannot be undone.')) {
      setLeads(leads.filter(lead => lead.id !== leadId));
      toast.success('Lead deleted successfully!');
    }
  };

  const handleStatusChange = (leadId, newStatus) => {
    setLeads(leads.map(lead => 
      lead.id === leadId ? { ...lead, status: newStatus } : lead
    ));
    toast.success('Lead status updated successfully!');
  };

  const getFilteredLeads = () => {
    const today = new Date();
    let filteredLeads = leads.filter(lead => {
      const leadDate = new Date(lead.date);
      
      // Date filtering
      let dateMatch = true;
      switch (selectedFilter) {
        case 'all':
          dateMatch = true;
          break;
        case 'today':
          dateMatch = leadDate.toDateString() === today.toDateString();
          break;
        case 'yesterday':
          const yesterday = new Date(today);
          yesterday.setDate(yesterday.getDate() - 1);
          dateMatch = leadDate.toDateString() === yesterday.toDateString();
          break;
        case 'last7days':
          const weekAgo = new Date(today);
          weekAgo.setDate(weekAgo.getDate() - 7);
          dateMatch = leadDate >= weekAgo;
          break;
        case 'last30days':
          const monthAgo = new Date(today);
          monthAgo.setDate(monthAgo.getDate() - 30);
          dateMatch = leadDate >= monthAgo;
          break;
        case 'last90days':
          const threeMonthsAgo = new Date(today);
          threeMonthsAgo.setDate(threeMonthsAgo.getDate() - 90);
          dateMatch = leadDate >= threeMonthsAgo;
          break;
        case 'thismonth':
          dateMatch = leadDate.getMonth() === today.getMonth() && leadDate.getFullYear() === today.getFullYear();
          break;
        case 'lastmonth':
          const lastMonth = new Date(today);
          lastMonth.setMonth(lastMonth.getMonth() - 1);
          dateMatch = leadDate.getMonth() === lastMonth.getMonth() && leadDate.getFullYear() === lastMonth.getFullYear();
          break;
        case 'custom':
          if (customDateFrom && customDateTo) {
            dateMatch = leadDate >= new Date(customDateFrom) && leadDate <= new Date(customDateTo);
          }
          break;
        default:
          dateMatch = true;
      }

      // Search filtering
      const matchesSearch = searchTerm === '' || 
        lead.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        lead.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        lead.company?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        lead.phone?.includes(searchTerm);

      // Status filtering
      const matchesStatus = filterStatus === 'All' || lead.status === filterStatus;

      // Source filtering
      const matchesSource = filterSource === 'All' || lead.source === filterSource;

      return dateMatch && matchesSearch && matchesStatus && matchesSource;
    });
    
    return filteredLeads;
  };

  const filteredLeads = getFilteredLeads();

  const getStatusColor = (status) => {
    switch (status) {
      case 'New': return '#3B82F6';
      case 'In Progress': return '#F59E0B';
      case 'Qualified': return '#10B981';
      case 'Converted': return '#8B5CF6';
      case 'Lost': return '#EF4444';
      default: return '#6B7280';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High': return '#EF4444';
      case 'Medium': return '#F59E0B';
      case 'Low': return '#10B981';
      default: return '#6B7280';
    }
  };

  return (
    <>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
        <h1>Client Leads</h1>
        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          <select 
            value={selectedFilter} 
            onChange={(e) => setSelectedFilter(e.target.value)}
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd', minWidth: '200px' }}
          >
            {filterOptions.map(option => (
              <option key={option.value} value={option.value}>
                {option.label} {option.value === 'all' ? `(${leads.length})` : ''}
              </option>
            ))}
          </select>
          <button
            onClick={handleAddClientClick}
            style={{ 
              padding: '0.5rem 1rem', 
              borderRadius: '4px', 
              border: 'none', 
              backgroundColor: '#28a745',
              color: 'white',
              cursor: 'pointer',
              fontWeight: 'bold',
              boxShadow: 'none',
              transition: 'none'
            }}
            onMouseEnter={(e) => e.target.style.boxShadow = 'none'}
            onMouseLeave={(e) => e.target.style.boxShadow = 'none'}
          >
            Add Client
          </button>
        </div>
      </div>
      
      {/* Custom Date Range Section */}
      {selectedFilter === 'custom' && (
        <div className="dashboard-card" style={{ marginBottom: '1rem' }}>
          <h3>Custom Date Range</h3>
          <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
            <input
              type="date"
              value={customDateFrom}
              onChange={(e) => setCustomDateFrom(e.target.value)}
              style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
              placeholder="From Date"
            />
            <span>to</span>
            <input
              type="date"
              value={customDateTo}
              onChange={(e) => setCustomDateTo(e.target.value)}
              style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
              placeholder="To Date"
            />
          </div>
        </div>
      )}

      {/* Add Client Form */}
      {showAddForm && (
        <div className="dashboard-card" style={{ marginBottom: '1rem' }}>
          <h3>Add New Client</h3>
          <form onSubmit={handleAddClient} style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
              <div>
                <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>Name *</label>
                <input
                  type="text"
                  value={newClient.name}
                  onChange={(e) => setNewClient({...newClient, name: e.target.value})}
                  required
                  style={{ width: '100%', padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
                  placeholder="Enter client name"
                />
              </div>
              <div>
                <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>Email *</label>
                <input
                  type="email"
                  value={newClient.email}
                  onChange={(e) => setNewClient({...newClient, email: e.target.value})}
                  required
                  style={{ width: '100%', padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
                  placeholder="Enter email address"
                />
              </div>
            </div>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
              <div>
                <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>Phone</label>
                <input
                  type="tel"
                  value={newClient.phone}
                  onChange={(e) => setNewClient({...newClient, phone: e.target.value})}
                  style={{ width: '100%', padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
                  placeholder="Enter phone number"
                />
              </div>
              <div>
                <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>Source</label>
                <select
                  value={newClient.source}
                  onChange={(e) => setNewClient({...newClient, source: e.target.value})}
                  style={{ width: '100%', padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
                >
                  <option value="Website">Website</option>
                  <option value="Referral">Referral</option>
                  <option value="Social Media">Social Media</option>
                  <option value="Email Campaign">Email Campaign</option>
                  <option value="LinkedIn">LinkedIn</option>
                  <option value="Other">Other</option>
                </select>
              </div>
            </div>
            <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end' }}>
              <button
                type="button"
                onClick={() => setShowAddForm(false)}
                style={{ 
                  padding: '0.5rem 1rem', 
                  borderRadius: '4px', 
                  border: '1px solid #ddd', 
                  backgroundColor: '#f5f5f5',
                  cursor: 'pointer'
                }}
              >
                Cancel
              </button>
              <button
                type="submit"
                style={{ 
                  padding: '0.5rem 1rem', 
                  borderRadius: '4px', 
                  border: 'none', 
                  backgroundColor: '#007bff',
                  color: 'white',
                  cursor: 'pointer'
                }}
              >
                Add Client
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Leads List */}
      <div className="dashboard-card">
        <h3>Leads List ({filteredLeads.length} leads)</h3>
        {filteredLeads.length > 0 ? (
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '1rem' }}>
              <thead>
                <tr style={{ backgroundColor: '#f5f5f5' }}>
                  <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Name</th>
                  <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Email</th>
                  <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Phone</th>
                  <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Status</th>
                  <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Date</th>
                  <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Source</th>
                </tr>
              </thead>
              <tbody>
                {filteredLeads.map(lead => (
                  <tr key={lead.id} style={{ borderBottom: '1px solid #eee' }}>
                    <td style={{ padding: '0.75rem' }}>{lead.name}</td>
                    <td style={{ padding: '0.75rem' }}>{lead.email}</td>
                    <td style={{ padding: '0.75rem' }}>{lead.phone}</td>
                    <td style={{ padding: '0.75rem' }}>
                      <span style={{
                        padding: '0.25rem 0.5rem',
                        borderRadius: '12px',
                        fontSize: '0.8rem',
                        backgroundColor: 
                          lead.status === 'New' ? '#e3f2fd' :
                          lead.status === 'In Progress' ? '#fff3e0' :
                          lead.status === 'Qualified' ? '#e8f5e8' :
                          '#f3e5f5',
                        color: 
                          lead.status === 'New' ? '#1976d2' :
                          lead.status === 'In Progress' ? '#f57c00' :
                          lead.status === 'Qualified' ? '#388e3c' :
                          '#7b1fa2'
                      }}>
                        {lead.status}
                      </span>
                    </td>
                    <td style={{ padding: '0.75rem' }}>{new Date(lead.date).toLocaleDateString()}</td>
                    <td style={{ padding: '0.75rem' }}>{lead.source}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p style={{ textAlign: 'center', color: '#666', marginTop: '2rem' }}>
            No leads found for the selected filter.
          </p>
        )}
      </div>
    </>
  );
}

export default ClientLeads;
