import React, { useState } from 'react';
import { CiSearch } from 'react-icons/ci';
import './UsersRoles.css';

function UsersRoles() {
  const [users, setUsers] = useState([
    { 
      id: 1, 
      name: 'Rajat Panwar', 
      email: 'rajat@noxtm.com', 
      role: 'Admin', 
      status: 'Active',
      access: ['Data Cluster', 'Projects', 'Finance', 'Digital Media', 'Marketing']
    },
    { 
      id: 2, 
      name: 'Sarah Johnson', 
      email: 'sarah.johnson@company.com', 
      role: 'Project Manager', 
      status: 'Active',
      access: ['Projects']
    },
    { 
      id: 3, 
      name: 'Mike Davis', 
      email: 'mike.davis@company.com', 
      role: 'Social Media Manager', 
      status: 'Active',
      access: ['Digital Media', 'Marketing']
    },
    { 
      id: 4, 
      name: 'Emily Brown', 
      email: 'emily.brown@company.com', 
      role: 'Graphic Designer', 
      status: 'Active',
      access: ['Projects', 'Digital Media']
    },
    { 
      id: 5, 
      name: 'David Wilson', 
      email: 'david.wilson@company.com', 
      role: 'Web Developer', 
      status: 'Active',
      access: ['Projects', 'Data Cluster']
    },
    { 
      id: 6, 
      name: 'Lisa Anderson', 
      email: 'lisa.anderson@company.com', 
      role: 'Data Analyst', 
      status: 'Active',
      access: ['Data Cluster', 'Finance']
    },
    { 
      id: 7, 
      name: 'Tom Miller', 
      email: 'tom.miller@company.com', 
      role: 'Human Resource', 
      status: 'Active',
      access: ['Projects', 'Finance']
    },
    { 
      id: 8, 
      name: 'Kate Taylor', 
      email: 'kate.taylor@company.com', 
      role: 'Data Miner', 
      status: 'Active',
      access: ['Data Cluster']
    },
    { 
      id: 9, 
      name: 'Alex Garcia', 
      email: 'alex.garcia@company.com', 
      role: 'SEO Manager', 
      status: 'Active',
      access: ['Digital Media', 'Marketing']
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  const roles = [
    'Admin',
    'Project Manager', 
    'Data Miner',
    'Data Analyst',
    'Social Media Manager',
    'Human Resource',
    'Graphic Designer',
    'Web Developer',
    'SEO Manager'
  ];

  const handleRoleChange = (userId, newRole) => {
    setUsers(users.map(user => 
      user.id === userId ? { ...user, role: newRole } : user
    ));
  };

  const handleStatusToggle = (userId) => {
    setUsers(users.map(user => 
      user.id === userId ? { 
        ...user, 
        status: user.status === 'Active' ? 'Inactive' : 'Active' 
      } : user
    ));
  };

  const handleAccessAdd = (userId) => {
    // This would typically open a modal or dropdown to add access
    console.log('Add access for user:', userId);
  };

  const handleAccessRemove = (userId, accessToRemove) => {
    setUsers(users.map(user => 
      user.id === userId ? { 
        ...user, 
        access: user.access.filter(access => access !== accessToRemove)
      } : user
    ));
  };

  const getAccessColor = (access) => {
    const colors = {
      'Data Cluster': '#8B5CF6',
      'Projects': '#10B981',
      'Finance': '#EF4444',
      'Digital Media': '#3B82F6',
      'Marketing': '#F59E0B'
    };
    return colors[access] || '#6B7280';
  };

  const filteredUsers = users.filter(user => 
    user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.role.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="users-roles-container">
      <div className="users-roles-header">
        <div className="header-content">
          <div className="header-left">
            <h2>Users & Roles Management</h2>
            <p>Manage user accounts, roles, and permissions across your organization.</p>
          </div>
          <div className="header-right">
            <div className="user-count">
              <span className="count-label">All users</span>
              <span className="count-number">{users.length}</span>
            </div>
            <div className="search-container">
              <CiSearch className="search-icon" />
              <input
                type="text"
                placeholder="Search"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>
          </div>
        </div>
      </div>

      <div className="users-table-container">
        <table className="users-table">
          <thead>
            <tr>
              <th className="checkbox-column">
                <input type="checkbox" className="select-all-checkbox" />
              </th>
              <th>User Details</th>
              <th>Access</th>
              <th>Role</th>
              <th>Status</th>
              <th className="actions-column">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map(user => (
              <tr key={user.id} className={user.status === 'Inactive' ? 'inactive-user' : ''}>
                <td className="checkbox-column">
                  <input type="checkbox" className="user-checkbox" />
                </td>
                <td className="user-details">
                  <div className="user-info">
                    <div className="user-avatar">
                      <div className="avatar-icon">ðŸ‘¤</div>
                    </div>
                    <div className="user-text">
                      <div className="user-name">{user.name}</div>
                      <div className="user-email">{user.email}</div>
                    </div>
                  </div>
                </td>
                <td className="user-access">
                  <div className="access-tags">
                    {user.access.map((access, index) => (
                      <span 
                        key={index} 
                        className="access-tag"
                        style={{ backgroundColor: getAccessColor(access) }}
                      >
                        {access}
                      </span>
                    ))}
                    <button 
                      className="add-access-btn"
                      onClick={() => handleAccessAdd(user.id)}
                    >
                      +
                    </button>
                  </div>
                </td>
                <td className="user-role">
                  <select 
                    value={user.role} 
                    onChange={(e) => handleRoleChange(user.id, e.target.value)}
                    className="role-select"
                  >
                    {roles.map(role => (
                      <option key={role} value={role}>{role}</option>
                    ))}
                  </select>
                </td>
                <td className="user-status">
                  <span className={`status-badge ${user.status.toLowerCase()}`}>
                    {user.status}
                  </span>
                </td>
                <td className="user-actions">
                  <button className="actions-menu-btn">
                    â‹¯
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

    </div>
  );
}

export default UsersRoles;
